DELIMITER $$

DROP FUNCTION FN_GET_COMMON_CODE;

CREATE FUNCTION FN_GET_COMMON_CODE (P_COMM_GRP_ID VARCHAR(100),P_COMM_CD_ID VARCHAR(100),P_RETVAL VARCHAR(50))
RETURNS VARCHAR(100)
	BEGIN
		DECLARE COMM_VAL VARCHAR(100);
		SET COMM_VAL = '';
		
		SELECT	COMM_CD_NM
		INTO 	COMM_VAL
		FROM	CC_COMMON_CODE
		WHERE 	1=1
		AND 	COMM_GRP_ID = 'BMCDW_INSTCD'
		AND 	COMM_CD_ID = '032';

        RETURN COMM_VAL;
   end;
        
DELIMITER ;


